  //     console.log("sedddd");
  //     try {
  //       const response = await axios.post('/restaurant-addMenu', data, {
  //         headers: {
  //           "Content-Type": 'multipart/form-data'
  //         }
  //       })
  //       console.log(response);
  //     } catch (error) {
  //       console.log(error);
  //     }
  //   }
  // }

  const submitMenu = async (data) => {
  
  data.set('restaurantId', restaurantId)
    console.log(data);
      try {
        const response = await axios.post('/restaurant-addMenu', data, {
          headers: {
            "Content-Type": 'multipart/form-data'
          }
        })
        console.log(response);
      } catch (error) {
        console.log(error);
      }
    }
  
  return (
    <div className='container'>
      <div className="create-menu">
        <h1>Add Menu</h1>
        <form action="/restaurant-addMenu" id='form-menu' onSubmit={submitMenu} >
          <label htmlFor="">Item Name</label>
          <input type="text" name="itemName" id="" {...register('itemName', { required: true })}/>
          <label htmlFor="">Category</label>
          <input type="text" name="category" id="" {...register('category', { required: true })} />
          <label htmlFor="">Price</label>
          <input type="text" name="price" id="" {...register('price', { required: true })}/>
          <label htmlFor="">Image</label>
          <input type="file" name="menu" {...register('menu', { required: true })}
            id="" />
          <div className="buttons">
            <input type="submit" value="Submit" />
            <input type="button" value="Finish" />
          </div>
        </form>
      </div>
    </div>
  )
}

export default CreateMenu
