import React, { useState ,useEffect} from 'react'
import './restaurant-panel.css'

import SpaceDashboardOutlinedIcon from '@mui/icons-material/SpaceDashboardOutlined';
import RoomServiceOutlinedIcon from '@mui/icons-material/RoomServiceOutlined';
import MenuBookOutlinedIcon from '@mui/icons-material/MenuBookOutlined';
import ChatBubbleOutlineOutlinedIcon from '@mui/icons-material/ChatBubbleOutlineOutlined';
import ManageAccountsOutlinedIcon from '@mui/icons-material/ManageAccountsOutlined';
import LogoutIcon from '@mui/icons-material/Logout';
import { Link } from '@mui/material';
function RestaurantPanel() {
  const [close, setClose] = useState(false)
  const [focus,setFocus] = useState(false)
  const handleSidebar = ()=>{
    setClose(true)
  }
 
// Attach the event listener when the component mounts
useEffect(() => {
  document.addEventListener('click', handleOutsideClick);

  // Clean up the event listener when the component unmounts
  return () => {
    document.removeEventListener('click', handleOutsideClick);
  };
}, []);
  return (
    <div className='restaurent-control-panel'> 
      <div className={`control ${close ? 'close':'open'}`}>
        <div className='restaurant-profile'>
            {/* <img src={restImg} alt="" /> */}
            <h2>HungryHub</h2>
        </div>
        <div className='list'>
        <ul className='restaurant-management'>
         <li tabIndex="0" className={`restaurant-element ${focus ? 'focus': ''} `}><Link tabIndex="0" onClick={handleFocus}><SpaceDashboardOutlinedIcon className='custom-icon'/> <span> Dashboard</span></Link></li>
         <li className='restaurant-element'><Link tabIndex="0"><RoomServiceOutlinedIcon className='custom-icon'/> <span> Order</span></Link></li>
         <li className='restaurant-element'><Link tabIndex="0"><MenuBookOutlinedIcon className='custom-icon'/> <span> Menu</span></Link></li>
         <li className='restaurant-element'><Link tabIndex="0"><ChatBubbleOutlineOutlinedIcon className='custom-icon'/> <span> Messages</span></Link></li>
         <li className='restaurant-element'><Link tabIndex="0"><ManageAccountsOutlinedIcon className='custom-icon'/> <span>Profile</span></Link></li>
        </ul>
        {/* <KeyboardDoubleArrowRightIcon/> */}
        
        {/* <button className='arrow' onClick={handleSidebar}><KeyboardDoubleArrowLeftIcon /></button> */}
          <ul className='restaurant-management'>
            <li className='restaurant-element' id='logout'><Link tabIndex="0"><LogoutIcon className='custom-icon'/><span>Logout</span></Link></li>
          </ul>
        </div>
      </div>
      <div></div>
    </div>
  )
}

export default RestaurantPanel
