import React, { useState } from 'react'
import './side-bar.css'
import SpaceDashboardOutlinedIcon from '@mui/icons-material/SpaceDashboardOutlined';
import RoomServiceOutlinedIcon from '@mui/icons-material/RoomServiceOutlined';
import MenuBookOutlinedIcon from '@mui/icons-material/MenuBookOutlined';
import ChatBubbleOutlineOutlinedIcon from '@mui/icons-material/ChatBubbleOutlineOutlined';
import ManageAccountsOutlinedIcon from '@mui/icons-material/ManageAccountsOutlined';
import LogoutIcon from '@mui/icons-material/Logout';
import { Link } from '@mui/material';

function RestaurantSidebar() {

    const [selectedElement, setSelectedElement] = useState(null);
    const handleLinkClick = (event) => {
      const clickedElement = event.currentTarget;
    
      // Remove the selection class from the previously selected element
      if (selectedElement) {
        selectedElement.classList.remove('active');
      }
    
      // Apply the selection class to the clicked element
      clickedElement.classList.add('active');
    
      // Update the selected element state
      setSelectedElement(clickedElement);
    };
  
   
  return (
    < >
  <div className={`control ${close ? 'close':'open'}`}>
        <div className='restaurant-profile'>
            {/* <img src={restImg} alt="" /> */}
            <h2>HungryHub</h2>
        </div>
        <div className='list'>
        <ul className='restaurant-management'>
         <li tabIndex="0" className= 'restaurant-element'><Link tabIndex="0" onClick={handleLinkClick}><SpaceDashboardOutlinedIcon className='custom-icon'/> <span> Dashboard</span></Link></li>
         <li className='restaurant-element'><Link tabIndex="0" onClick={handleLinkClick}><RoomServiceOutlinedIcon className='custom-icon'/> <span> Order</span></Link></li>
         <li className='restaurant-element'><Link tabIndex="0" onClick={handleLinkClick}><MenuBookOutlinedIcon className='custom-icon'/> <span> Menu</span></Link></li>
         <li className='restaurant-element'><Link tabIndex="0" onClick={handleLinkClick}><ChatBubbleOutlineOutlinedIcon className='custom-icon'/> <span> Messages</span></Link></li>
         <li className='restaurant-element'><Link tabIndex="0" onClick={handleLinkClick}><ManageAccountsOutlinedIcon className='custom-icon'/> <span>Profile</span></Link></li>
        </ul>
        {/* <KeyboardDoubleArrowRightIcon/> */}
        
        {/* <button className='arrow' onClick={handleSidebar}><KeyboardDoubleArrowLeftIcon /></button> */}
          <ul className='restaurant-management'>
            <li className='restaurant-element' id='logout'><Link tabIndex="0"><LogoutIcon className='custom-icon'/><span>Logout</span></Link></li>
          </ul>
        </div>
      </div>
  </>
  )
}

export default RestaurantSidebar
