import React, { useState } from 'react'
import './restaurant-panel.css'
import restImg from '../../images/pexels-rajesh-tp-1624487.jpg'
import SpaceDashboardOutlinedIcon from '@mui/icons-material/SpaceDashboardOutlined';
import RoomServiceOutlinedIcon from '@mui/icons-material/RoomServiceOutlined';
import MenuBookOutlinedIcon from '@mui/icons-material/MenuBookOutlined';
import ChatBubbleOutlineOutlinedIcon from '@mui/icons-material/ChatBubbleOutlineOutlined';
import ManageAccountsOutlinedIcon from '@mui/icons-material/ManageAccountsOutlined';
import KeyboardDoubleArrowRightIcon from '@mui/icons-material/KeyboardDoubleArrowRight';
import KeyboardDoubleArrowLeftIcon from '@mui/icons-material/KeyboardDoubleArrowLeft';
function RestaurantPanel() {
  const [close, setClose] = useState(false)
  const handleSidebar = ()=>{
    setClose(true)
  }
  return (
    <div className='restaurent-control-panel'> 
      <div className={`control ${close ? 'close':'open'}`}>
        <div className='restaurant-profile'>
            <img src={restImg} alt="" />
            <h4>IRANI RESTAURANT</h4>
        </div>
        <div className='list'>
        <ul className='restaurant-management'>
            <span><SpaceDashboardOutlinedIcon className='custom-icon' /> Dashboard</span>
            <span><RoomServiceOutlinedIcon className='custom-icon'/> Order</span>
            <span><MenuBookOutlinedIcon className='custom-icon'/> Menu</span>
            <span><ChatBubbleOutlineOutlinedIcon className='custom-icon'/> Messages</span>
            <span><ManageAccountsOutlinedIcon className='custom-icon'/> Profile</span>
        </ul>
        {/* <KeyboardDoubleArrowRightIcon/> */}
        
        <button className='arrow' onClick={handleSidebar}><KeyboardDoubleArrowLeftIcon /></button>
        </div>
      </div>
      <div></div>
    </div>
  )
}

export default RestaurantPanel
