import React, { useState } from 'react'
import './restaurant-panel.css'

import SpaceDashboardOutlinedIcon from '@mui/icons-material/SpaceDashboardOutlined';
import RoomServiceOutlinedIcon from '@mui/icons-material/RoomServiceOutlined';
import MenuBookOutlinedIcon from '@mui/icons-material/MenuBookOutlined';
import ChatBubbleOutlineOutlinedIcon from '@mui/icons-material/ChatBubbleOutlineOutlined';
import ManageAccountsOutlinedIcon from '@mui/icons-material/ManageAccountsOutlined';
import LogoutIcon from '@mui/icons-material/Logout';
import { Link } from '@mui/material';
function RestaurantPanel() {
  const [close, setClose] = useState(false)
  const [focus,setFocus] = useState(false)
  const handleSidebar = ()=>{
    setClose(true)
  }
  const [isClicked, setIsClicked] = useState(false);

  const [selectedElement, setSelectedElement] = useState(null);
  const handleLinkClick = (event) => {
    const clickedElement = event.currentTarget;
  
    // Remove the selection class from the previously selected element
    if (selectedElement) {
      selectedElement.classList.remove('active');
    }
  
    // Apply the selection class to the clicked element
    clickedElement.classList.add('active');
  
    // Update the selected element state
    setSelectedElement(clickedElement);
  };

 

  return (
    <div className='restaurent-control-panel'> 
 
      <div></div>
    </div>
  )
}

export default RestaurantPanel
