  const location = useLocation()
  const menu=location.state.menu
  console.log(menu);
  const [menuList,setMenuList]= useState(menu)
  return (
    <div className='container' id='menu-list'>
      <div className="restaurant-data">
        <h2>Irani Restaurant</h2>
        <h4>Chinese, North Indian</h4>
      </div>

        {
          menuList.map((menu)=>{
            return(
              <div className="menu-data">
        <div className='menu-item'>
        <h3>{menu.item}</h3>
        <h5>price</h5>
        </div>
        <div className="menu-image">
            <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSgxT0AXcdV4W7cSEtsESBPAO17_PMcpRySxg&usqp=CAU" alt="" />
            <div className="add-buttons">
            <button className="click-add">ADD</button>
            {/* <button type='button' id='minus'>-</button>
            <span> 7</span>
            <button type='button' id='add'>+</button> */}

            </div>
        </div>
      </div>
        
      )
    })
  }
      
    </div>
  )
}

export default MenuList
