import React, { useEffect, useState } from 'react'
import './restaurants.css'
function Restaurants() {

  const [restaurants, setRestaurants] = useState(
    [{
      name: "Mothers's Kitchen",
      deliveryTime: 30,
      relevance: 4,
      rating: 4,
      cost: 200,
      description: 'North Indian, South Indian',
      image: "https://images.pexels.com/photos/1640772/pexels-photo-1640772.jpeg?cs=srgb&dl=pexels-ella-olsson-1640772.jpg&fm=jpg"
    },
    {
      name: 'Zam Zam Hotel',
      deliveryTime: 25,
      relevance: 5,
      rating: 4.5,
      cost: 150,
      description: 'Chinese, North indian,South Indian',
      image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR7ZQweZXJuwPizDP_oV1Qg27jrme9FQ9IXBw&usqp=CAUg'
    },
    {
      name: 'The Imperial Kitchen',
      deliveryTime: 35,
      relevance: 4.7,
      rating: 4.2,
      cost: 500,
      description: 'Chinese, North indian,South Indian',
      image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS5dAG2vG0JeLl3OyKe6zEolYkZ-J42xFka7Q&usqp=CAU'
    },
    {
      name: 'Al Baik Feasto Express',
      deliveryTime: 40,
      relevance: 5,
      rating: 5,
      cost: 400,
      description: 'Chinese, North indian,South Indian',
      image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRxy-tj1SxZnIVfGDtpj-r4ymeRpAruN2493w&usqp=CAU'
    },
    {
      name: 'Malabar Kitchen',
      deliveryTime: 38,
      relevance: 4.9,
      rating: 4.5,
      cost: 200,
      description: 'Chinese, North indian,South Indian',
      image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRAVlCf-w-_qejk3uh_42d-FW1TIPGxOPV95Q&usqp=CAU'
    },
    {
      name: 'Tiffanys',
      deliveryTime: 38,
      relevance: 4.0,
      rating: 4.0,
      cost: 250,
      description: 'Chinese, North indian,South Indian',
      image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT8ZwZ2tuTvWJXRIRDBjpHM--O-J6sULrMbJA&usqp=CAU'
    },
    {
      name: 'Urban Kitchen',
      deliveryTime: 38,
      relevance: 4.9,
      rating: 4.5,
      cost: 200,
      description: 'Chinese, North indian,South Indian',
      image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQeSJPz3XVGSzvDc5MB81h-tpOgc0Okk24QMw&usqp=CAU'
    },
    {
      name: 'Urban Kitchen',
      deliveryTime: 38,
      relevance: 4.9,
      rating: 4.5,
      cost: 200,
      description: 'Chinese, North indian,South Indian',
      image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQeSJPz3XVGSzvDc5MB81h-tpOgc0Okk24QMw&usqp=CAU'
    }, {
      name: 'Urban Kitchen',
      deliveryTime: 38,
      relevance: 4.9,
      rating: 4.5,
      cost: 200,
      description: 'Chinese, North indian,South Indian',
      image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQeSJPz3XVGSzvDc5MB81h-tpOgc0Okk24QMw&usqp=CAU'
    },
    ])

    useEffect(() => {
      fetch("http://localhost:9000/users")
      .then(res=>res.json())
      .then(r)

      
      const sortedRestaurantsList = [...restaurants].sort((a, b) => b.relevance - a.relevance);
      setRestaurants(sortedRestaurantsList);
    }, []);
  const filterRestaurants = (sortOption) => {
    let sortedRestaurantsList = [];
    switch (sortOption) {
      case 'Relevance':
        sortedRestaurantsList = [...restaurants].sort((a, b) => b.relevance - a.relevance);
        break;
      case 'Delivery Time':
        sortedRestaurantsList = [...restaurants].sort((a, b) => a.deliveryTime - b.deliveryTime);
        break;
      case 'Rating':
        sortedRestaurantsList = [...restaurants].sort((a, b) => b.rating - a.rating);
        break;
      case 'Cost: Low To High':
        sortedRestaurantsList = [...restaurants].sort((a, b) => a.cost - b.cost);
        break;
      case 'Cost: High To Low':
        sortedRestaurantsList = [...restaurants].sort((a, b) => b.cost - a.cost);
        break;
      default:
        sortedRestaurantsList = [...restaurants].sort((a, b) => b.relevance - a.relevance);
        break;
    }
    setRestaurants(sortedRestaurantsList)
  }


  return (
    <div className='container'>
      <div className='filters'>
        <h1>373 restaurants</h1>
        <div className='filter-options'>
          <ul className='filter-options'>
          <li><button onClick={(e) => {
            const filterValue = e.target.textContent
            filterRestaurants(filterValue)
          }}>Relevance</button></li>
         <li> <button onClick={(e) => {
            const filterValue = e.target.textContent
            filterRestaurants(filterValue)
          }}>Delivery Time</button></li>
          <li><button onClick={(e) => {
            const filterValue = e.target.textContent
            filterRestaurants(filterValue)
          }}>Rating</button></li>
        <li><button onClick={(e) => {
            const filterValue = e.target.textContent
            filterRestaurants(filterValue)
          }}>Cost: Low To High</button></li>
         <li><button onClick={(e) => {
            const filterValue = e.target.textContent
            filterRestaurants(filterValue)
          }}>Cost: High To Low</button></li>

          </ul>
          <button id='filter-icon'><span>Filters</span> <i className="fa-solid fa-sliders fa-rotate-90"></i></button>
        </div>
      </div>
      <div className='restaurants'>

        {restaurants.map((restautent => {
          return (

            <div className="restaurant-details">

              <img src={restautent.image} alt="" />
              <h3>{restautent.name}</h3>
              <p>{restautent.description}</p>
              <div className='restaurant-review'>
                <div id='rate'>
                  <i className="fa-solid fa-star"></i>
                  <span>{restautent.rating}</span>
                </div>
                <span>{restautent.deliveryTime} minutes</span>
                <span id='currency'><svg xmlns="http://www.w3.org/2000/svg" height="48" viewBox="0 96 960 960" width="48"><path d="M564 936 290 652v-66h130q57 0 100-37t50-103H240v-60h325q-13-48-53.5-79T420 276H240v-60h480v60H566q23 20 39 51t23 59h92v60h-90q-8 91-67.5 145.5T420 646h-52l279 290h-83Z" /></svg>{restautent.cost} fOR TWO</span>
              </div>
              <hr />
              <button>QUICK VIEW</button>
            </div>
          )
        }))}



      </div>
    </div>
  )
}

export default Restaurants
