import React, { useState } from 'react'
import './menu.css'
import { useLocation } from 'react-router-dom'
import axios from '../../axios'

function MenuList() {
  const location = useLocation()
  const menu = location.state.menu
  console.log(menu);
  const [menuList, setMenuList] = useState(menu)
  const [add, setAdd] = useState(true)
  const [count,setCount] = useState([{id:'',qty:0}])
   
  const addItemToCart =(menu)=>{
    // console.log(menu._id);
    axios.post(`/users/menu/${menu.restaurantId}`,menu).then((response)=>{
      if (menu._id===count.id) {
        setAdd(!add)
        
      }
    const updateArray= [
      ...ount,
      {id:menu._id,qty: count.qty+1}]
     setCount()
      
       
    }).catch((err)=>{
        console.log(err);
    })
  }
  const handleIncrement =()=>{
    axios.post('/user/menu/add-count',count).then(()=>{

    })
  }


  return (
    <div className='container' id='menu-list'>
      <div className="restaurant-data">
        <h2>Irani Restaurant</h2>
        <h4>Chinese, North Indian</h4>
      </div>

      {
        menuList.map((menu) => {
          return (
            <div className="menu-data" key={menu._id}>
              <div className='menu-item'>
                <h3>{menu.itemName}</h3>
                <h5>{menu.price}</h5>
              </div>
              <div className="menu-image">
                <img src={`http://localhost:9000/${menu.imageName}`} alt="" />
                <div className="add-buttons">
                  {count.qty==0 ? (<button className="click-add" onClick={() => {
                     addItemToCart(menu)
                   }} >ADD</button>)
                    : (
                      <>
                        <button id='minus' className='click-minus'><i className="fa-solid fa-minus"></i></button>
                        <span>{count.id===menu._id && count.qty}</span>
                        <button id="add " onClick={handleIncrement} className='click-add'><i className="fa-solid fa-plus"></i></button>
                      </>
                    )}
                </div>
              </div>
            </div>

          )
        })
      }

    </div>
  )
}

export default MenuList
