import React, { useState } from 'react'
import './menu.css'
import { useLocation } from 'react-router-dom'
function MenuList() {
  const location = useLocation()
  const menu = location.state.menu
  console.log(menu);
  const [menuList, setMenuList] = useState(menu)
  const [add, setAdd] = useState(!true)
  return (
    <div className='container' id='menu-list'>
      <div className="restaurant-data">
        <h2>Irani Restaurant</h2>
        <h4>Chinese, North Indian</h4>
      </div>

      {
        menuList.map((menu) => {
          return (
            <div className="menu-data">
              <div className='menu-item'>
                <h3>{menu.itemName}</h3>
                <h5>{menu.price}</h5>
              </div>
              <div className="menu-image">
                <img src={`http://localhost:9000/${menu.imageName}`} alt="" />
                <div className="add-buttons">
                  {add ? (<button className="click-add" onClick={() => {
                     setAdd(true) 
                     additemT
                   }} >ADD</button>)
                    : (
                      <>
                        <button id='minus' className='click-minus'><i class="fa-solid fa-minus"></i></button>
                        <span>2</span>
                        <button id="add "  className='click-add'><i class="fa-solid fa-plus"></i></button>
                      </>
                    )}
                </div>
              </div>
            </div>

          )
        })
      }

    </div>
  )
}

export default MenuList
