import React, { useState } from 'react';
import './App.css';
import Home from './pages/user/Home'
import Register from './pages/admin/RestaurantRegister'
import RestaurantAddMenu from './pages/admin/RestaurantAddMenu';
import Menu from './pages/user/Menu'
import UserSignUP from './pages/user/UserSignUP';
import UserLogin from './pages/user/UserLogin';
import AddToCart from './pages/user/AddToCart';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'
import { UserLoginContext } from './components/context/useContext';
function App() {
  const [loggedIn,set] = useState(null)
  return (
    <div className="App">
      <Router>
        <Routes>
          <Route path='/restaurant-register' element={<Register />} />
          <Route path='/restaurant-addMenu' element={<RestaurantAddMenu />} />
          <Route path='/signup' element={<UserSignUP />} />
         
          <Route path='/' element={<UserLoginContext.Provider><Home /></UserLoginContext.Provider> } />
          <Route path='/login' element={<UserLoginContext.Provider><UserLogin /></UserLoginContext.Provider> } />
          <Route path='/menu/:restId' element={<UserLoginContext.Provider><Menu /></UserLoginContext.Provider>} />
          <Route path='/add-to-cart/:userId' element={<UserLoginContext><AddToCart/></UserLoginContext>}/>
      
        </Routes>
      </Router>
    </div>
  );
}

export default App;
