import React, { useEffect, useState } from 'react'
import './menu.css'
import { useLocation } from 'react-router-dom'
import axios from '../../axios'


function MenuList() {
  const location = useLocation()
  const menu = location.state.menu
  const userId = location.state.userId
  const [menuList, setMenuList] = useState(menu)
  const [count, setCount] = useState({})
  const [cartItem,setCartItem] = useState([])
  const addItemToCart = (menu, number) => {
    axios.post(`/users/menu/${menu.restaurantId}`, { menu, number, userId }).then((response) => {
      console.log(response.data);
      const result = response.data.result
      console.log(result);
   cartItem.map((item) => {
   if (item.itemId === result.itemId) {
    console.log('eeee');
     setCartItem((prevCount) => ({
       ...prevCount,
       quantity:  result.quantity 

      }))
  }   
   } );


    }).catch((err) => {
      console.log(err);
    })
  }
  useEffect(()=>{
    axios.post('/users/get-count',{userId}).then((response)=>{
      console.log(cartItem);
      console.log(count);
      setCartItem(response.data.cartItem)
      console.log(cartItem);

    })
  })
  return (
    <div className='container' id='menu-list'>
      <div className="restaurant-data">
        <h2>Irani Restaurant</h2>
        <h4>Chinese, North Indian</h4>
      </div>

    
                       
      {menuList.map((menu) => {

   const s = cartItem !== null && Array.isArray(cartItem)
? cartItem.find((item) => item.itemId === menu._id)
: null;
 
  return (
    <div className="menu-data" key={menu._id}>
      <div className='menu-item'>
        <h3>{menu.itemName}</h3>
        <h5>{menu.price}</h5>
      </div>
      <div className="menu-image">
        <img src={`http://localhost:9000/${menu.imageName}`} alt="" />
        <div className="add-buttons">
          {cartItemExists && cartItemExists.quantity !== 0 ? (
            <>
              <button id='minus' onClick={() => addItemToCart(menu, -1)} className='click-minus'><i className="fa-solid fa-minus"></i></button>
              <span>{cartItemExists.quantity}</span>
              <button id="add" onClick={() => addItemToCart(menu, 1)} className='click-add'><i className="fa-solid fa-plus"></i></button>
            </>
          ) : (
            <button className="click-add" onClick={() => addItemToCart(menu, 1)}>ADD</button>
          )}
        </div>
      </div>
    </div>
  );
})}

    </div>
  )
}

export default MenuList
