import React from 'react'
import "./create-menu.css"
import axios from '../../axios';
import { useLocation } from 'react-router-dom'
import { useForm } from 'react-hook-form';
import schema from '../../validation/formValidation'

function CreateMenu() {
  
  const { register, handleSubmit, formState: { errors } } = useForm({
    resolver: schema
 })

  const location = useLocation();
  const restaurantId = location.state.id;
  console.log(restaurantId);

  // const submitMenu = async (e) => {
  //   e.preventDefault();
  //   const form = e.target;
  //   const formData = new FormData(form)
  //   formData.set('restaurantId', restaurantId)
  //   const data = Object.fromEntries(formData)
  //   console.log(data);
  //   if (Object.values(data).some((value) => value)) {
  //     console.log("sedddd");
  //     try {
  //       const response = await axios.post('/restaurant-addMenu', data, {
  //         headers: {
  //           "Content-Type": 'multipart/form-data'
  //         }
  //       })
  //       console.log(response);
  //     } catch (error) {
  //       console.log(error);
  //     }
  //   }
  // }

  const submitMenu = async (e) => {
    e.preventDefault();
    const form = e.target;
    const formData = new FormData(form)
    formData.set('restaurantId', restaurantId)
    const data = Object.fromEntries(formData)
    console.log(data);
    if (Object.values(data).some((value) => value)) {
      console.log("sedddd");
      try {
        const response = await axios.post('/restaurant-addMenu', data, {
          headers: {
            "Content-Type": 'multipart/form-data'
          }
        })
        console.log(response);
      } catch (error) {
        console.log(error);
      }
    }
  }
  return (
    <div className='container'>
      <div className="create-menu">
        <h1>Add Menu</h1>
        <form action="/restaurant-addMenu" id='form-menu' onSubmit={submitMenu} >
          <label htmlFor="">Item Name</label>
          <input type="text" name="itemName" id="" />
          <label htmlFor="">Category</label>
          <input type="text" name="category" id="" />
          <label htmlFor="">Price</label>
          <input type="text" name="price" id="" />
          <label htmlFor="">Image</label>
          <input type="file" name="menu"
            id="" />
          <div className="buttons">
            <input type="submit" value="Submit" />
            <input type="button" value="Finish" />
          </div>
        </form>
      </div>
    </div>
  )
}

export default CreateMenu
