import React from 'react'
import './register.css'
import { useNavigate } from 'react-router-dom';
import axios from '../../axios';
import {useForm} from 'react-hook-form';
import schema from '../../validation/formValidation'

function Register() {
   const {register,handleSubmit,formState:{errors}} = useForm({
      resolver:schema
   })
   const navigate = useNavigate()

   // const submitRegister = async (e) => {
   //    e.preventDefault()
   //    const form = e.target

   //    const formData = new FormData(form)
   //    let data = Object.fromEntries(formData)
   //    if (Object.values(data).some((value) => value)) {
   //       try {
   //          const response = await axios.post('/restaurant-register', data);
   //          const id= response.data.id;
   //          // console.log(id); 
   //          navigate('/restaurant-addmenu',{state:{id}})
   //       } catch (error) {
   //          console.error(error);
   //       }
   //    }
   // }
   const submitRegister = async (data) => {
     
         console.log(data);
     
         try {
            const response = await axios.post('/restaurant-register', data);
            const id= response.data.id;
            // console.log(id); 
            navigate('/restaurant-addmenu',{state:{id}})
         } catch (error) {
            console.error(error);
         }
      
   }
   return (
      <div className='conatiner'>
         <div className='row'>
            <div className='img'>
               <h1>Partner with Us</h1>
               <span>Get listed on India's leading online food delivery marketplace today</span>
            </div>

            <div className='registration-form'>
               <form action="" onSubmit={handleSubmit(submitRegister)}>
                  <h2>Restaurant Information</h2>
                  <div className='info'>
                     <label htmlFor="">Restaurant Name</label>
                     <input type="text" {...register('RestaurantName',{required:true})} />
                     <p>{er}</p>
                  </div>
                  <div className='info'>
                     <label htmlFor="">Restaurant Address</label>
                     <input type="text" {...register('RestaurantAddress',{required:true})} />
                  </div>
                  <div className='info'>
                     <label htmlFor="">Owner Number</label>
                     <input type="text" {...register('OwnerNumber',{required:true})} />
                  </div>
                  <div className='info'>
                     <label htmlFor="">Cuisine Type</label>
                     <input type="text" {...register('CuisineType',{required:true})} />
                  </div>
                  <input type="submit" value="Add Restaurent" />
               </form>
            </div>
         </div>
      </div>
   )
}

export default Register
